/* =================================================================================================================================================================================
-- Server		: LOCAL SERVER
-- Database		: User Database
-- Script		: DifferentTablesTypes
-- Description	: List of All Possibilities of Different Combinations of Tables that can be Used
-- Possibilities:
--  TableType10 Contains All Cols as PrimaryKey Cols Only But No IdentityKey Col (i.e., No Other Cols Except PK Cols)
--  TableType11 Contains PrimaryKey Cols Along with Other Cols But No IdentityKey Col
--  TableType12 Contains All Cols as PrimaryKey Cols and Computed Cols Only But No IdentityKey Col(i.e., No Other Cols Except PK Cols and Comp Cols)
--  TableType13 Contains PrimaryKey Cols Along with Other Cols and Computed Cols also But No IdentityKey Col
--
--**TableType20 Contains IdentityKey Col Only But No PrimaryKey Cols (i.e., No Other Cols Except ID Col) --Invalid Table(No Use Case for this Table Type)
--  TableType21 Contains IdentityKey Col Along with Other Cols But No PrimaryKey Cols
--**TableType22 Contains IdentityKey Col and Computed Cols Only But No PrimaryKey Cols (i.e., No Other Cols Except ID Col and Comp Cols) --Invalid Table
--  TableType23 Contains IdentityKey Col Along with Other Cols and Computed Cols also But No PrimaryKey Cols
--
--  TableType30 Contains All Cols as PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col (i.e., No Other Cols Except PK and ID Cols)
--  TableType31 Contains PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col Along with Other Cols
--  TableType32 Contains All Cols as PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col and Computed Cols Only (i.e., No Other Cols Except PK, ID and Comp Cols)
--  TableType33 Contains PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col Along with Other Cols and Computed Cols also
--
--  TableType40 Contains All Cols with Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col (i.e., No Other Cols Except PK and ID Cols)
--  TableType41 Contains Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col Along with Other Cols
--  TableType42 Contains All Cols with Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col and Computed Cols Only (i.e., No Other Cols Except PK, ID and Comp Cols)
--  TableType43 Contains Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col Along with Other Cols and Computed Cols also
--
-- *TableType50 DoesNot Contain Both PrimaryKey Cols and IdentityKey Col (i.e, Just like Heap and might not be Heap if clustered Index is Created)
-- *TableType51 DoesNot Contain Both PrimaryKey Cols and IdentityKey Col and have Computed Cols (i.e, Just like Heap and might not be Heap if clustered Index is Created)
================================================================================================================================================================================= */
DECLARE @InsertData BIT = 1;
SET NOCOUNT ON;
--TableType10	: Contains All Cols as PrimaryKey Cols Only But No IdentityKey Col (i.e., No Other Cols Except PK Cols)
--$BeginRegion: TableType10
IF OBJECT_ID('dbo.TableType10', 'U') IS NOT NULL
	DROP TABLE dbo.TableType10;
CREATE TABLE dbo.TableType10 (A INT NOT NULL, B INT NOT NULL);
ALTER TABLE dbo.TableType10
ADD CONSTRAINT PK10 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType10(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType10
--
--TableType11	: Contains PrimaryKey Cols Along with Other Cols But No IdentityKey Col
--$BeginRegion: TableType11
IF OBJECT_ID('dbo.TableType11', 'U') IS NOT NULL
	DROP TABLE dbo.TableType11;
CREATE TABLE dbo.TableType11 (A INT NOT NULL, B INT NOT NULL, X CHAR(1) NULL);
ALTER TABLE dbo.TableType11
ADD CONSTRAINT PK11 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType11(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType11
--
--TableType12	: Similar to TableType10 along with Computed Cols
--$BeginRegion: TableType12
IF OBJECT_ID('dbo.TableType12', 'U') IS NOT NULL
	DROP TABLE dbo.TableType12;
CREATE TABLE dbo.TableType12 (A INT NOT NULL, B INT NOT NULL, P AS A + B, Q AS A * B);
ALTER TABLE dbo.TableType12
ADD CONSTRAINT PK12 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType12(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType12
--
--TableType13	: Similar to TableType11 along with Computed Cols
--$BeginRegion: TableType13
IF OBJECT_ID('dbo.TableType13', 'U') IS NOT NULL
	DROP TABLE dbo.TableType13;
CREATE TABLE dbo.TableType13 (A INT NOT NULL, B INT NOT NULL, P AS A + B, Q AS A * B, X CHAR(1) NULL);
ALTER TABLE dbo.TableType13
ADD CONSTRAINT PK13 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType13(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType13
--
--
--TableType20	: Contains IdentityKey Col Only But No PrimaryKey Cols (i.e., No Other Cols Except ID Col) --Invalid Table(No Use Case for this Table Type)
--$BeginRegion: TableType20
IF OBJECT_ID('dbo.TableType20', 'U') IS NOT NULL
	DROP TABLE dbo.TableType20;
CREATE TABLE dbo.TableType20 (A INT IDENTITY(1, 1));
--
IF @InsertData = 1
BEGIN
	SET IDENTITY_INSERT dbo.TableType20 ON;
	INSERT	INTO dbo.TableType20(A)
	VALUES(1), (2);
	SET IDENTITY_INSERT dbo.TableType20 OFF;
END;
--$EndRegion: TableType20
--
--TableType21	: Contains IdentityKey Col Along with Other Cols But No PrimaryKey Cols
--$BeginRegion: TableType21
IF OBJECT_ID('dbo.TableType21', 'U') IS NOT NULL
	DROP TABLE dbo.TableType21;
CREATE TABLE dbo.TableType21 (A INT IDENTITY(1, 1), X CHAR(1) NULL);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType21(X)
	VALUES('A'), ('B'), ('C'), ('D');
--$EndRegion: TableType21
--
--TableType22	: Similar to TableType20 along with Computed Cols --**Invalid Table
--$BeginRegion: TableType22
IF OBJECT_ID('dbo.TableType22', 'U') IS NOT NULL
	DROP TABLE dbo.TableType22;
CREATE TABLE dbo.TableType22 (A INT IDENTITY(1, 1), P AS A + A);
--
IF @InsertData = 1
BEGIN
	SET IDENTITY_INSERT dbo.TableType22 ON;
	INSERT	INTO dbo.TableType22(A)
	VALUES(1), (2);
	SET IDENTITY_INSERT dbo.TableType22 OFF;
END;
--$EndRegion: TableType22
--
--TableType23	: Similar to TableType21 along with Computed Cols
--$BeginRegion: TableType23
IF OBJECT_ID('dbo.TableType23', 'U') IS NOT NULL
	DROP TABLE dbo.TableType23;
CREATE TABLE dbo.TableType23 (A INT IDENTITY(1, 1), P AS A + A, X CHAR(1) NULL);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType23(X)
	VALUES('A'), ('B'), ('C'), ('D');
--$EndRegion: TableType23
--
--
--TableType30	: Contains All Cols as PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col (i.e., No Other Cols Except PK and ID Cols)
--$BeginRegion: TableType30
IF OBJECT_ID('dbo.TableType30', 'U') IS NOT NULL
	DROP TABLE dbo.TableType30;
CREATE TABLE dbo.TableType30 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1));
ALTER TABLE dbo.TableType30
ADD CONSTRAINT PK30 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType30(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType30
--
--TableType31	: Contains PrimaryKey Cols in which One of the PrimaryKey Col is an IdentityKey Col Along with Other Cols
--$BeginRegion: TableType31
IF OBJECT_ID('dbo.TableType31', 'U') IS NOT NULL
	DROP TABLE dbo.TableType31;
CREATE TABLE dbo.TableType31 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1),  X CHAR(1) NULL);
ALTER TABLE dbo.TableType31
ADD CONSTRAINT PK31 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType31(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType31
--
--TableType33	: Similar to TableType30 along with Computed Cols
--$BeginRegion: TableType32
IF OBJECT_ID('dbo.TableType32', 'U') IS NOT NULL
	DROP TABLE dbo.TableType32;
CREATE TABLE dbo.TableType32 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1), P AS A + B, Q AS A * B);
ALTER TABLE dbo.TableType32
ADD CONSTRAINT PK32 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType32(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType32
--
--TableType33	: Similar to TableType31 along with Computed Cols
--$BeginRegion: TableType33
IF OBJECT_ID('dbo.TableType33', 'U') IS NOT NULL
	DROP TABLE dbo.TableType33;
CREATE TABLE dbo.TableType33 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1), P AS A + B, Q AS A * B, X CHAR(1) NULL);
ALTER TABLE dbo.TableType33
ADD CONSTRAINT PK33 PRIMARY KEY CLUSTERED(A, B);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType33(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType33
--
--
--TableType40	: Contains All Cols with Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col (i.e., No Other Cols Except PK and ID Cols)
--$BeginRegion: TableType40
IF OBJECT_ID('dbo.TableType40', 'U') IS NOT NULL
	DROP TABLE dbo.TableType40;
CREATE TABLE dbo.TableType40 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1));
ALTER TABLE dbo.TableType40
ADD CONSTRAINT PK40 PRIMARY KEY CLUSTERED(A, B, C);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType40(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType40
--
--TableType41	: Contains Both Exclusive PrimaryKey Cols and Exclusive IdentityKey Col Along with Other Cols
--$BeginRegion: TableType41
IF OBJECT_ID('dbo.TableType41', 'U') IS NOT NULL
	DROP TABLE dbo.TableType41;
CREATE TABLE dbo.TableType41 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1), X CHAR(1) NULL);
ALTER TABLE dbo.TableType41
ADD CONSTRAINT PK41 PRIMARY KEY CLUSTERED(A, B, C);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType41(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType41
--
--TableType42	: Similar to TableType40 along with Computed Cols
--$BeginRegion: TableType42
IF OBJECT_ID('dbo.TableType42', 'U') IS NOT NULL
	DROP TABLE dbo.TableType42;
CREATE TABLE dbo.TableType42 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1), P AS A + B, Q AS A * B);
ALTER TABLE dbo.TableType42
ADD CONSTRAINT PK42 PRIMARY KEY CLUSTERED(A, B, C);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType42(A, B)
	VALUES(0, 0), (0, 1), (1, 0), (1, 1);
--$EndRegion: TableType42
--
--TableType43	: Similar to TableType41 along with Computed Cols
--$BeginRegion: TableType43
IF OBJECT_ID('dbo.TableType43', 'U') IS NOT NULL
	DROP TABLE dbo.TableType43;
CREATE TABLE dbo.TableType43 (A INT NOT NULL, B INT NOT NULL, C INT NOT NULL IDENTITY(1, 1), P AS A + B, Q AS A * B, X CHAR(1) NULL);
ALTER TABLE dbo.TableType43
ADD CONSTRAINT PK43 PRIMARY KEY CLUSTERED(A, B, C);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType43(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D');
--$EndRegion: TableType43
--
--
--TableType50	: DoesNot Contain Both PrimaryKey Cols and IdentityKey Col (i.e, Just like Heap and might not be Heap if clustered Index is Created)
--$BeginRegion: TableType50
IF OBJECT_ID('dbo.TableType50', 'U') IS NOT NULL
	DROP TABLE dbo.TableType50;
CREATE TABLE dbo.TableType50 (A INT NULL, B INT NULL, X CHAR(1) NULL);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType50(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D'), (NULL, 0, 'E'), (NULL, 1, 'F'), (0, NULL, 'G'), (1, NULL, 'H'), (NULL, NULL, 'I');
--$EndRegion: TableType50
--
--TableType51	: Similar to TableType50 along with Computed Cols
--$BeginRegion: TableType51
IF OBJECT_ID('dbo.TableType51', 'U') IS NOT NULL
	DROP TABLE dbo.TableType51;
CREATE TABLE dbo.TableType51 (A INT NULL, B INT NULL, P AS A + B, Q AS A * B, X CHAR(1) NULL);
--
IF @InsertData = 1
	INSERT	INTO dbo.TableType51(A, B, X)
	VALUES(0, 0, 'A'), (0, 1, 'B'), (1, 0, 'C'), (1, 1, 'D'), (NULL, 0, 'E'), (NULL, 1, 'F'), (0, NULL, 'G'), (1, NULL, 'H'), (NULL, NULL, 'I');
--$EndRegion: TableType51
