/*Method 1*/
USE master;
GO
DECLARE @DBname VARCHAR(50) = '$database_name$';
IF EXISTS (SELECT name FROM sys.databases WHERE name = @DBname)
BEGIN
	--$BeginRegion: Kill Specific Database All Sessions
	PRINT N'--KILLING SPIDs in "' + @DBname + '"';
	DECLARE @KillSPIDs NVARCHAR(MAX);
	SELECT @KillSPIDs = COALESCE(@KillSPIDs, '') + 'KILL ' + CONVERT(VARCHAR(8), session_id) + ';'
	FROM master.sys.dm_exec_sessions
	WHERE session_id > 50 AND session_id <> @@SPID AND database_id = DB_ID(@DBname);
	--PRINT @KillSPIDs
	EXEC sys.sp_executesql @command = @KillSPIDs;
	--$EndRegion: Kill Specific Database All Sessions
	--
	DECLARE @SQLcmd NVARCHAR(MAX);
	SET @SQLcmd = 'ALTER DATABASE [' + @DBname + '] SET SINGLE_USER WITH ROLLBACK IMMEDIATE;';
	EXEC sys.sp_executesql @command = @SQLcmd;
	SET @SQLcmd = 'DROP DATABASE [' + @DBname + ']';
	EXEC sys.sp_executesql @command = @SQLcmd;
END;
GO
--
--
/*Method 2*/
WHILE EXISTS (SELECT NULL FROM sys.databases WHERE name = '$database_name$')
BEGIN
	USE master;
	--$BeginRegion: Kill Specific Database All Sessions
	PRINT N'--KILLING SPIDs in "$database_name$"';
	DECLARE @KillSPIDs NVARCHAR(MAX) = N'';
	SELECT @KillSPIDs = @KillSPIDs + CHAR(10) + N'Kill ' + CONVERT(VARCHAR(5), spid) + N';'
	FROM master.sys.sysprocesses
	WHERE spid > 50 AND  spid <> @@SPId AND dbid = DB_ID('$database_name$');
	PRINT @KillSPIDs;
	EXEC sys.sp_executesql @statement = @KillSPIDs;
	--$EndRegion: Kill Specific Database All Sessions	
	ALTER DATABASE [$database_name$] SET SINGLE_USER WITH ROLLBACK IMMEDIATE;
	DROP DATABASE [$database_name$];
END;
GO
