/* =============================================================================================================================================================
-- Server		: LOCALSERVER
-- Database		: master
-- Script		: Generate_Database_Backup_and_Restore_Script.sql
-- Description	: In Dev Env, There might be required to Backup and Restore Database Multiple Times.
--	So, This will Generate Script for Backup and Restore to Original File Locations.
--	This Backup will be a Copy_Only Backup to Avoid "Breaking LSN Chain".
-- Usage		: Provide the @DBname, @BackupLocation, @BackupDescription (optional)
--PreRequisities:
----$BeginRegion: Enable CMDshell and Create Folders
	EXEC sys.sp_configure @configname = 'show advanced options', @configvalue = '1'; RECONFIGURE;
	EXEC sys.sp_configure @configname = 'xp_cmdshell', @configvalue = '1'; RECONFIGURE;
	EXEC sys.sp_configure @configname = 'show advanced options', @configvalue = '0'; RECONFIGURE;
	EXEC sys.xp_cmdshell @command = 'mkdir "C:\SQL\Backups\"';
	EXEC sys.xp_cmdshell @command = 'mkdir "C:\SQL\MSSQL\DATA\"';
	EXEC sys.xp_cmdshell @command = 'mkdir "C:\SQL\MSSQL\LOG\"';
----$EndRegion: Enable CMDshell and Create Folders
--Note			: _v = SQLversion | @BackupDescription | _d = Date (YYYYMMDD) | _t = Time (hhmmss)
============================================================================================================================================================= */
USE master;
GO
--
DECLARE @DBname VARCHAR(256) = '$DatabaseName$';
DECLARE @BackupFileLocation VARCHAR(1024) = '$BackupFileLocation$'; --Most Preffered Path "C:\SQL\Backups" --Refer PreRequisities
DECLARE @BackupDescription VARCHAR(32) = '$BackupDescription$'; --Pls DoNot Put Spaces in @BackupDescription
DECLARE @RestoreDBasReplica BIT = 1; --Enable If we Want to Restore the Main Database as Copy Database
DECLARE @RestoreDBasReplicaName VARCHAR(32) = '$ReplicaName$'; --Will be Restored as @DBname + '_' + @RestoreDBasReplicaName
DECLARE @IncludeTimeStampInFileName BIT = 0; --Enable If We are Making Multiple Backups On the Same Day
--
/* ===== ** DO NOT CHANGE CODE BELOW FROM HERE ** =========================================================================================================== */
IF DB_ID(@DBname) IS NULL
BEGIN
	RAISERROR('Database Does Not Exists, Please check the "@DBname" and Try Again...!', 16, 1);
	RETURN;
END;
--
IF LTRIM(RTRIM(@BackupDescription)) = '' OR @BackupDescription = 'BackupDescription' SET @BackupDescription = NULL;
DECLARE @BackupFileName VARCHAR(255);
SET @BackupFileName = REPLACE(@@SERVERNAME, '\', '-') + '_v' + CAST(SERVERPROPERTY('ProductVersion') AS VARCHAR(4)); --Adding Main SQL Version
SET @BackupFileName = @BackupFileName + '_' + REPLACE(@DBname, ' ', '-'); --Adding ServerName, DatabaseName
SET @BackupFileName = @BackupFileName + '_' + ISNULL(@BackupDescription, 'FullCopyOnly'); --Adding Description of Backup
SET @BackupFileName = @BackupFileName + '_d' + CONVERT(VARCHAR(8), GETDATE(), 112); --Adding Date Stamp
IF @IncludeTimeStampInFileName = 1 SET @BackupFileName = @BackupFileName + '_t' + REPLACE(CONVERT(VARCHAR(9), GETDATE(), 108), ':', ''); --Adding Time Stamp
SET @BackupFileName = @BackupFileName + '.bak'; --Adding Backup Extension
--
--$BeginRegion: Backup Script Generation
PRINT N'/* ===== ** Backup Script for Database: ' + @DBname + ' ** ======================================================================================== */';
PRINT N'USE [master];';
PRINT N'GO';
--
PRINT N'--$BeginRegion: COPY_ONLY Backup Script for Database "' + @DBname + '"';
PRINT N'BACKUP DATABASE [' + @DBname + ']';
PRINT N'TO DISK = N''' + @BackupFileLocation + '\' + @BackupFileName + '''';
PRINT N'WITH NAME = N''' + @DBname + '-Full Copy Only Database Backup'',';
PRINT N'	COPY_ONLY, NOFORMAT, INIT, SKIP, NOREWIND, NOUNLOAD, COMPRESSION, CHECKSUM, STATS = 5;';
PRINT N'GO';
PRINT N'RESTORE VERIFYONLY';
PRINT N'FROM DISK = N''' + @BackupFileLocation + '\' + @BackupFileName + '''';
PRINT N'WITH NOUNLOAD, NOREWIND;';
PRINT N'GO';
PRINT N'--$EndRegion: COPY_ONLY Backup Script for Database "' + @DBname + '"';
--$EndRegion: Backup Script Generation
--
PRINT '';
--
--$BeginRegion: Restore Script Generation
DECLARE @LogicalName VARCHAR(256), @PhysicalFileLocation VARCHAR(MAX), @PhysicalFileName VARCHAR(256);
--
----$BeginRegion: MainDB_RestoreScript
BEGIN
	PRINT N'/* ===== ** Restore Script for Database: ' + @DBname + ' ** ======================================================================================= */';
	IF @RestoreDBasReplica = 1 PRINT N'/* --Commented as Requested to Restore the Database "' + @DBname + '" as a Replica(Copy) Database:';
	PRINT N'USE [master];';
	PRINT N'GO';
	--
	PRINT N'--$BeginRegion: Killing All Sessions for Database "' + @DBname + '"';
	PRINT N'DECLARE @KillSPIDs NVARCHAR(MAX) = N'''';';
	PRINT N'SELECT @KillSPIDs = @KillSPIDs + CHAR(10) + N''KILL '' + CONVERT(VARCHAR(5), session_id) + N'';''';
	PRINT N'FROM sys.dm_exec_sessions';
	PRINT N'WHERE session_id > 50 AND session_id <> @@SPID AND DB_NAME(database_id) = ''' + @DBname + ''';';
	PRINT N'----PRINT @KillSPIDs;';
	PRINT N'EXEC sys.sp_executesql @command = @KillSPIDs;';
	PRINT N'GO';
	PRINT N'--$EndRegion: Killing All Sessions for Database "' + @DBname + '"';

	DECLARE MainDB_FileCursor CURSOR FAST_FORWARD READ_ONLY LOCAL FOR
	SELECT name AS LogicalName,
			--physical_name AS PhysicalName,
			SUBSTRING(physical_name, 1, (LEN(physical_name) - CHARINDEX('\', REVERSE(physical_name), 1))) AS PhysicalFileLocation,
			SUBSTRING(physical_name, (LEN(physical_name) - CHARINDEX('\', REVERSE(physical_name), 1) + 2), LEN(physical_name)) AS PhysicalFileName
	FROM sys.master_files
	WHERE DB_NAME(database_id) = @DBname
	ORDER BY type ASC, data_space_id ASC;

	PRINT '';
	PRINT N'--$BeginRegion: Restoring Database "' + @DBname + '" From Backup "' + @BackupFileLocation + '\' + @BackupFileName + '"';
	PRINT N'RESTORE DATABASE [' + @DBname + ']';
	PRINT N'FROM DISK = N''' + @BackupFileLocation + '\' + @BackupFileName + '''';
	PRINT N'WITH ';

	OPEN MainDB_FileCursor;
	FETCH NEXT FROM MainDB_FileCursor INTO @LogicalName, @PhysicalFileLocation, @PhysicalFileName;

	WHILE @@FETCH_STATUS = 0
	BEGIN
		PRINT N'	MOVE N''' + @LogicalName + ''' TO N''' + @PhysicalFileLocation + '\' + @PhysicalFileName + ''',';
		--
		FETCH NEXT FROM MainDB_FileCursor INTO @LogicalName, @PhysicalFileLocation, @PhysicalFileName;
	END;

	PRINT N'NOUNLOAD, REPLACE, STATS = 5;';
	PRINT N'GO';
	PRINT N'--$EndRegion: Restoring Database "' + @DBname + '" From Backup "' + @BackupFileLocation + '\' + @BackupFileName + '"';

	CLOSE MainDB_FileCursor;
	DEALLOCATE MainDB_FileCursor;
	IF @RestoreDBasReplica = 1 PRINT N'*/';
END;
----$EndRegion: MainDB_RestoreScript
--
PRINT '';
--
----$BeginRegion: ReplicaDB_RestoreScript
IF @RestoreDBasReplica = 1
BEGIN
	IF (LTRIM(RTRIM(@RestoreDBasReplicaName)) = '' OR @RestoreDBasReplicaName IS NULL) SET @RestoreDBasReplicaName = 'Copy';
	--$BeginRegion: Kill Sessions and Drop Replica Database as @RestoreDBasReplica = 1
	PRINT N'/* ===== ** Restore Script for Database: ' + @DBname + '_' + @RestoreDBasReplicaName + ' ** ================================================================================== */';
	PRINT N'USE [master];';
	PRINT N'GO';
	--
	PRINT N'--$BeginRegion: Killing All Sessions for Database "' + @DBname + '_' + @RestoreDBasReplicaName + '"';
	PRINT N'DECLARE @KillSPIDs NVARCHAR(MAX) = N'''';';
	PRINT N'SELECT @KillSPIDs = @KillSPIDs + CHAR(10) + N''KILL '' + CONVERT(VARCHAR(5), session_id) + N'';''';
	PRINT N'FROM sys.dm_exec_sessions';
	PRINT N'WHERE session_id > 50 AND session_id <> @@SPID AND DB_NAME(database_id) = ''' + @DBname + '_' + @RestoreDBasReplicaName + ''';';
	PRINT N'----PRINT @KillSPIDs;';
	PRINT N'EXEC sys.sp_executesql @command = @KillSPIDs;';
	PRINT N'GO';
	PRINT N'--$EndRegion: Killing All Sessions for Database "' + @DBname + '_' + @RestoreDBasReplicaName + '"';
	PRINT '';
	PRINT N'--$BeginRegion: Dropping Database "' + @DBname + '_' + @RestoreDBasReplicaName + '" to Restore Fresh DB Replica';
	PRINT N'IF EXISTS (SELECT name FROM sys.databases WHERE name = ''' + @DBname + '_' + @RestoreDBasReplicaName + ''')';
	PRINT N'BEGIN';
	PRINT N'	ALTER DATABASE [' + @DBname + '_' + @RestoreDBasReplicaName + '] SET SINGLE_USER WITH ROLLBACK IMMEDIATE;';
	PRINT N'	DROP DATABASE [' + @DBname + '_' + @RestoreDBasReplicaName + '];';
	PRINT N'END;';
	PRINT N'--$EndRegion: Dropping Database "' + @DBname + '_' + @RestoreDBasReplicaName + '" to Restore Fresh DB Replica';
	--$EndRegion: Kill Sessions and Drop Replica Database as @RestoreDBasReplica = 1
	--
	DECLARE ReplicaDB_FileCursor CURSOR FAST_FORWARD READ_ONLY LOCAL FOR
	SELECT name AS LogicalName,
		   --physical_name AS PhysicalName,
		   SUBSTRING(physical_name, 1, (LEN(physical_name) - CHARINDEX('\', REVERSE(physical_name), 1))) AS PhysicalFileLocation,
		   SUBSTRING(physical_name, (LEN(physical_name) - CHARINDEX('\', REVERSE(physical_name), 1) + 2), LEN(physical_name)) AS PhysicalFileName
	FROM sys.master_files
	WHERE DB_NAME(database_id) = @DBname
	ORDER BY type ASC, data_space_id ASC;

	PRINT '';
	PRINT N'--$BeginRegion: Restoring Database "' + @DBname + '_' + @RestoreDBasReplicaName + '" From Backup "' + @BackupFileLocation + '\' + @BackupFileName + '"';
	PRINT N'RESTORE DATABASE [' + @DBname + '_' + @RestoreDBasReplicaName + ']';
	PRINT N'FROM DISK = N''' + @BackupFileLocation + '\' + @BackupFileName + '''';
	PRINT N'WITH ';

	OPEN ReplicaDB_FileCursor;
	FETCH NEXT FROM ReplicaDB_FileCursor INTO @LogicalName, @PhysicalFileLocation, @PhysicalFileName;

	WHILE @@FETCH_STATUS = 0
	BEGIN
		IF @PhysicalFileName LIKE '%.mdf' --Primary Files
			PRINT N'	MOVE N''' + @LogicalName + ''' TO N''' + @PhysicalFileLocation + '\' + REPLACE(@PhysicalFileName, '.mdf', '_' + @RestoreDBasReplicaName + '.mdf') + ''',';
		IF @PhysicalFileName LIKE '%.ndf' --Secondary Files
			PRINT N'	MOVE N''' + @LogicalName + ''' TO N''' + @PhysicalFileLocation + '\' + REPLACE(@PhysicalFileName, '.ndf', '_' + @RestoreDBasReplicaName + '.ndf') + ''',';
		IF @PhysicalFileName LIKE '%.ldf' --Log Files
			PRINT N'	MOVE N''' + @LogicalName + ''' TO N''' + @PhysicalFileLocation + '\' + REPLACE(@PhysicalFileName, '.ldf', '_' + @RestoreDBasReplicaName + '.ldf') + ''',';
		--
		FETCH NEXT FROM ReplicaDB_FileCursor INTO @LogicalName, @PhysicalFileLocation, @PhysicalFileName;
	END;

	PRINT N'NOUNLOAD, REPLACE, STATS = 5;';
	PRINT N'GO';
	PRINT N'--$EndRegion: Restoring Database "' + @DBname + '_' + @RestoreDBasReplicaName + '" From Backup "' + @BackupFileLocation + '\' + @BackupFileName + '"';

	CLOSE ReplicaDB_FileCursor;
	DEALLOCATE ReplicaDB_FileCursor;
END;
----$EndRegion: ReplicaDB_RestoreScript
--$EndRegion: Restore Script Generation
