--$BeginRegion: Kill Specific Database All Sessions
DECLARE @KillSPIDs NVARCHAR(4000) = N'';
SET @KillSPIDs = N'--For SPIDs in "$database_name$"'
SELECT @KillSPIDs = @KillSPIDs + CHAR(10) + N'Kill ' + CONVERT(VARCHAR(5), session_id) + N';'
FROM sys.dm_exec_sessions 
WHERE session_id > 50 AND session_id <> @@SPID AND DB_NAME(database_id) = '$database_name$';
PRINT @KillSPIDs;
EXEC sys.sp_executesql @statement = @KillSPIDs;
--$EndRegion: Kill Specific Database All Sessions
