/* =========================================================================================================================================
-- Server		: Local Server
-- Database		: User Database
-- Script		: SearchTablesForString_v2
-- Description	: Search All Tables which Contain Text Columns like %char% and %text%.
-- Usage		: Most Efficient for Larger Tables Having TableRowCount >= 100000.
========================================================================================================================================= */
DECLARE @SearchString VARCHAR(1024);
SET @SearchString='$StringToSearch$'; --Provide the String here.
--
/* === DO NOT CHANGE BELOW CODE FROM HERE =============================================================================================== */
SET NOCOUNT ON;
--$BeginRegion: SearchResultSet TableVariable
PRINT --
N'IF OBJECT_ID(''tempdb..#SearchResults'',''U'') IS NOT NULL
	DROP TABLE #SearchResults;
GO
CREATE TABLE #SearchResults
(
	DatabaseName VARCHAR(256) NULL,
	SchemaName VARCHAR(256) NULL,
	TableName VARCHAR(256) NULL,
	ColumnName VARCHAR(256) NULL,
	ColumnDataType VARCHAR(256) NULL,
	TableRowCount INT NULL,
	StringOccuranceCount INT NULL
);
GO';
PRINT '--';
--$EndRegion: SearchResultSet TableVariable
--
--$BeginRegion: SearchTableColumn_Cursor
DECLARE @DBname VARCHAR(256), @SCHname VARCHAR(256), @TBLname VARCHAR(256), @COLname VARCHAR(256), @COLdatatype VARCHAR(256);
--
DECLARE @SQLcmd NVARCHAR(MAX) =NULL;
--
DECLARE SearchTableColumn_Cursor CURSOR LOCAL FAST_FORWARD FOR
SELECT COL.TABLE_CATALOG, COL.TABLE_SCHEMA, COL.TABLE_NAME, COL.COLUMN_NAME, COL.DATA_TYPE
FROM INFORMATION_SCHEMA.COLUMNS AS COL
INNER JOIN INFORMATION_SCHEMA.TABLES AS TBL ON TBL.TABLE_SCHEMA=COL.TABLE_SCHEMA AND TBL.TABLE_NAME=COL.TABLE_NAME 
WHERE TBL.TABLE_TYPE='BASE TABLE' AND COL.DATA_TYPE LIKE '%char%' OR COL.DATA_TYPE LIKE '%text%';
--
OPEN SearchTableColumn_Cursor;
FETCH NEXT FROM SearchTableColumn_Cursor INTO @DBname, @SCHname, @TBLname, @COLname, @COLdatatype;
--
WHILE @@FETCH_STATUS=0 BEGIN
	SET @SQLcmd --
		=N'INSERT INTO #SearchResults(DatabaseName, SchemaName, TableName, ColumnName, ColumnDataType, TableRowCount, StringOccuranceCount)'+CHAR(10) --
		 +N'SELECT '''+@DBname+N''' AS "DatabaseName",'+CHAR(10) --
		 +N'	'''+@SCHname+N''' AS "SchemaName",'+CHAR(10) --
		 +N'	'''+@TBLname+N''' AS "TableName",'+CHAR(10) --
		 +N'	'''+@COLname+N''' AS "ColumnName",'+CHAR(10) --
		 +N'	'''+@COLdatatype+N''' AS "ColumnDataType",'+CHAR(10) --
		 +N'	(SELECT COUNT(1) FROM ['+@DBname+N'].['+@SCHname+N'].['+@TBLname+N'] WITH (NOLOCK)) AS "TableRowCount",'+CHAR(10) --
		 +N'	COUNT(1) AS "StringOccuranceCount"'+CHAR(10) --
		 +N'FROM ['+@DBname+N'].['+@SCHname+N'].['+@TBLname+N'] WITH (NOLOCK)'+CHAR(10) --
		 +N'WHERE ['+@COLname+N'] LIKE ''%'+@SearchString+N'%'';'+CHAR(10) --
		 +N'GO';
	--
	PRINT @SQLcmd;
	--
	FETCH NEXT FROM SearchTableColumn_Cursor INTO @DBname, @SCHname, @TBLname, @COLname, @COLdatatype;
END;
CLOSE SearchTableColumn_Cursor;
DEALLOCATE SearchTableColumn_Cursor;
--$EndRegion: SearchTableColumn_Cursor
--
PRINT '--';
PRINT --
N'SELECT DatabaseName, SchemaName, TableName, ColumnName, ColumnDataType, TableRowCount, StringOccuranceCount,
	(''SELECT * FROM [''+DatabaseName+''].[''+SchemaName+''].[''+TableName+''] WHERE [''+ColumnName+''] LIKE ''''%'+@SearchString+'%'''';'') AS SearchQuery
FROM #SearchResults
WHERE StringOccuranceCount<>0;';
